INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (1, 'Месники: Завершення', '2019', 'Ентоні Руссо, Джо Руссо', 'Скарлетт Йоханссон, Кріс Хемсворт, Кріс Еванс, Роберт Дауні мл.', 'Кожна історія має своє завершення – попереду епічний фінал найвеличнішої історії у Всесвіті Marvel. Чи зможуть Месники зупинити Таноса, зважаючи на великі втрати у своїх рядах? І чи вдасться вціліти людству у цьому масштабному протистоянні?', 'static/img/poster_avengers.jpg');
INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (2, 'Мортал Комбат', '2021', 'Саймон МакКуойд', 'Льюїс Тан, Джош Ловсон, Джессіка Макнемі, Хіроюкі Санада', '«Мортал Комбат» – це історія про бійця MMA Коула Янґа, який звик терпіти побої заради грошей. Він нічого не знає про своє походження чи про те, чому імператор Зовнішнього Світу Шан Цзун послав свого найкращого воїна, загадкового кріоманта Саб-Зіро, його вполювати. Майор спецзагону Джакс, на тілі якого є такий самий дивний силует дракона, як і в Коула, радить йому відшукати Соню Блейд. Побоюючись за безпеку своєї родини, Коул вирушає на пошуки. Незабаром він опиняється у храмі Лорда Рейдена, Старшого бога та покровителя Землі, який дарує притулок усім, хто носить знак дракона. Коул тренується разом з досвідченими воїнами Лю Каном, Кун Лао і шахраюватим найманцем Кано. Разом з найвидатнішими чемпіонами Землі Янґ готується виступити проти ворогів із Зовнішнього Світу. Ставки високі, адже це битва за цілий всесвіт! Але чи зуміє Коул відкрити свою аркану – величезну силу в душі – вчасно, щоб врятувати не лише свою родину, але й зупинити Зовнішній Світ раз і назавжди?', 'static/img/poster_mortal_kombat.png');
INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (3, 'Тенет', '2020', 'Крістофер Нолан', 'Роберт Паттінсон, Аарон Тейлор-Джонсон, Елізабет Дебікі, Джон Девід Вашингтон', 'Новий фільм видатного Крістофера Нолана. Озброєний єдиним словом – Тенет – головний герой бореться за виживання усього світу. Він подорожує оповитим сутінками світом міжнародного шпигунства з єдиною місією, яка розгорнеться за межами реального часу. Це не подорож крізь час. Це інверсія.', 'static/img/poster_tenet.jpg');
INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (4, 'Гнів людський', '2021', 'Гай Річі', 'Джейсон Стейтем, Голт МакКеллені, Скотт Іствуд, Джош Гарнетт, Лаз Алонсо', 'Хілл- загадковий і холодний на вигляд британський джентльмен, але всередині нього палає жага правосуддя. Переслідуючи свої мотиви, він має намір знайти замовника серії багатомільйонних пограбувань, що приголомшили Лос-Анджелес. У цій заплутаній грі у кожного своя роль, але винні обов''язково пізнають гнів людський...', 'static/img/poster_wrath_of_man.jpg');
INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (5, 'Ніхто', '2020', 'Ілля Найшуллер', 'Боб Оденкірк, Конні Нільсен, Крістофер Ллойд', 'Непомітна людина живе звичайним життям, поки одного разу, рятуючи жінку від нападу бандитів, не надсилає одного з хуліганів в лікарню. Лише пізніше він дізнається, що це був брат впливового гангстера, який тепер жадає помсти.', 'static/img/poster_nobody.jpg');
INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (6, 'Форсаж 9', '2021', 'Джастін Лін', 'Він Дізель, Шарліз Терон, Джон Сіна', 'За сюжетом Домінік Торетто залягає на дно та живе спокійним життям з Летті та їхнім сином Брайаном. Проте доля має на нього зовсім інші плани, адже небезпека минулих помилок завжди ховається за мирним горизонтом. Небезпечна кібертерористка Сайфер вирішує помститися Домініку за попередні невдачі та використовує для цього професійного найманого вбивцю Джейкоба (Джон Сіна), який до того ж є давно покинутим братом Торетто. ', 'static/img/poster_fast_and_furious.jpg');
