INSERT INTO cinemax_db.movies (id, name, year, director, actors, description, img) VALUES (8, 'Круїз у Джунглях', '2021', 'Жауме Колєт-Серра', 'Двейн Джонсон, Емілі Блант, Едґар Рамірес, Пол Джаматті', 'Натхненний тематикою знаменитих парків «Діснейленд», «Круїз у джунглях» від Disney – це наповнена пригодами експедиція джунглями Амазонки за участю Двейна Джонсона, харизматичного капітана річкового човна, та Емілі Блант – відважної дослідниці дикої природи. Також у фільмі знімаються Едґар Рамірес, Джейк ВайтГолл, Джессі Племонс та Пол Джаматті.', 'static/img/poster_jungle_cruise.jpg');